import React, { Component, useState, useEffect, memo, useRef} from 'react'
import {Card, Col, Row, Badge, ModalBody, ModalTitle, Button, Modal, ModalDialog} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './home.css';
import axios from 'axios';
import { FaShareAlt } from "react-icons/fa";
import {FacebookShareButton, TwitterShareButton, EmailShareButton} from 'react-share';
import {EmailIcon,FacebookIcon,TwitterIcon} from 'react-share';
import { connect } from 'react-redux';
import BounceLoader from "react-spinners/BounceLoader";


const Home = props =>{

    const [newsAvailable, changeNewsState] = useState("Guardian")
    const [headlineNews, getNewsHeadlines] = useState([])
    const [isopenModal, changeModalState] = useState(false)
    const [activeItem, getActiveItem] = useState("")

    // Fetch Data
    const fetchData=()=>{
        if(props.whichNews === 'Guardian' || props.whichNews === 'guardian'){
        axios.get('http://localhost:5000/')
        .then(responseData => {
            console.log("Guardian",responseData.data);
            getNewsHeadlines(responseData.data)
            changeNewsState("Guardian")
        })
        .catch(responseData=>{
            console.log("error");
        })
    }
    else if(props.whichNews === 'nytimes'){
        axios.get('http://localhost:5000/nytimes')
        .then(responseData => {
            console.log("NYT",responseData.data);
            getNewsHeadlines(responseData.data)
            changeNewsState("nytimes")
        })
        .catch(responseData=>{
            console.log("error");
        })

    }
}

// Handling modal for sharing

    const handleClose = () =>{
        changeModalState(false)
    }

    const handleOpen = (title, weburl) =>{
        let sharedata = {title:title,webUrl:weburl}
        getActiveItem(sharedata)
        changeModalState(true)
    }

// Function to clear data if previous state changed

    const prevNews = usePrevious(props.whichNews);
    function usePrevious(value) {
        const ref = useRef()
        useEffect(() => {
        if(prevNews != props.whichNews){
           getNewsHeadlines([])
          ref.current = value;
          fetchData()}
        }, [props.whichNews]); // Only re-run if value changes
        return ref.current;
      }

            if (headlineNews.length==0){
                return(
                    <div className="sweet-loading" style = {{position:'fixed', top:'50%', left:'47%'}}>
                        <BounceLoader
                        style = {{display:'block', background:'#2b4fc4'}}
                        size={35}
                        color={"#2b4fc4"}
                        />
                    <span style={{position:'fixed',left:'46.5%', fontSize:'14px'}}>Loading</span>
                  </div>
                )
            }
            else{
                if((props.whichNews === 'guardian' || props.whichNews === 'Guardian') ){
                let newsCards = []
                let x = 0
                if(headlineNews.length!=0 && headlineNews[0].hasOwnProperty('blocks')){
                let newsCards = headlineNews.map(news => {
                            let color = "brown"
                            let text_color = "white"
                            if(news.sectionId == 'sport'){
                                color = '#f6c244';
                                text_color = 'black';
                            }
                            else if(news.sectionId == 'world'){
                                color = '#7c4eff';
                            }
                            else if(news.sectionId == 'technology'){
                                color = '#cedc39';
                                text_color = 'black';
                            }
                            else if(news.sectionId == 'politics'){
                                color = '#419488';
                            }
                            else if(news.sectionId == 'business'){
                                color = '#4696ec';
                            }
                            else {
                                color = '#6e757c';
                            }
                            // &&  !news.blocks.main.elements[0].assets.length == 0
                            if(news.blocks.hasOwnProperty('main')){
                            const newsTitle = news.webTitle;
                            const newsDescription = news.blocks.body[0].bodyTextSummary;
                            // const newsImage = news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file;
                            let imageList = []
                            if(news.blocks.main.elements[0].assets.length == 0){
                                imageList = ["https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png"]
                            }
                            else{
                                let filteredList = news.blocks.main.elements[0].assets.filter(image => {
                                    return image.typeData.width >= 2000
                                })
                                imageList = filteredList.map(filteredimg=>{
                                    return filteredimg.file
                                })
                                if(imageList.length == 0){
                                    imageList = ["https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png"]
                                }
                            }
                            const newsImage = imageList[0]

                            console.log("Image List",imageList)
                            // newsImageFilter 
                            const newsDate = news.webPublicationDate;
                            const newsBadge = news.sectionId;
                            const newsId = news.id;
                            const newsShareUrl = news.webUrl;
                            return (
                                <Link to={{ pathname: '/DetailedArticle/' + newsId ,state: {ArticleId: newsId}}} style={{ width:'100%', display:'block', textDecoration: 'none', color:'inherit' }}>
                                <div className="HomepageCard">
                                <Card style={{ width: '100%', marginTop:'30px', marginBottom:'30px', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)' }} >
                                        <Row>
                                            <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                                                <Card.Img className="img-responsive" variant="top" src={newsImage} style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                                            </Col>
                                            <Col lg={9} xs={12} className="mr-0 ml-0">
                                                <Card.Body>
                                                    <Card.Title><em>{newsTitle}<Link style={{zIndex:'1', position:'relative', textDecoration: 'none', color:'inherit'}} onClick={()=>handleOpen(newsTitle, newsShareUrl)}><FaShareAlt/></Link></em></Card.Title>
                                                    <Card.Text className="news-description">
                                                        {newsDescription}
                                                    </Card.Text>
                                                    <Card.Text>
                                                    <span> {newsDate} </span>
                                                    <Badge style={{backgroundColor: color, color:'white'}} className="float-right">
                                                        {newsBadge}
                                                    </Badge>
                                                    </Card.Text>
                                                </Card.Body>
                                            </Col>
                                        </Row>
                                    </Card>
                                </div>
                                </Link>
                                )
                            }
                        })
            return (
                <>
                    {newsCards}
                    <Modal show={isopenModal} onHide={()=>handleClose()}>
                            <Modal.Header closeButton>
                                <Modal.Title>{activeItem.title}</Modal.Title>
                            </Modal.Header>

                            <Modal.Body style={{marginTop:'1px',marginBottom:'1px', paddingBottom:'0px', fontSize:'20px'}} className="d-flex justify-content-center"><b>Share via</b></Modal.Body>
                            <Modal.Body className="d-flex justify-content-between">
                            <FacebookShareButton url={activeItem.webUrl} hashtag='CSCI_571_NewsApp' className="share">
                                <FacebookIcon size={70} className='float-left' round={true}/>
                            </FacebookShareButton>
                            <TwitterShareButton url={activeItem.webUrl} hashtags={['CSCI_571_NewsApp']}>
                                <TwitterIcon size={70} round={true}/>
                            </TwitterShareButton>
                            <EmailShareButton url={activeItem.webUrl} subject='#CSCI_571_NewsApp'>
                                <EmailIcon size={70} className='float-right' round={true}/>
                            </EmailShareButton>
                            </Modal.Body>
                    </Modal>
                </>
                );
            }
        else{
            let err = true
            if(err){
                return(
                    <div className="sweet-loading" style = {{position:'fixed', top:'50%', left:'47%'}}>
