import React, { Component } from 'react'
import {Card, Col, Row, Badge, Button, Accordion} from 'react-bootstrap';
import './detailed_page.css';
import axios from 'axios';
import {FacebookShareButton, TwitterShareButton, EmailShareButton} from 'react-share';
import {EmailIcon,FacebookIcon,TwitterIcon} from 'react-share';
import { FaChevronDown } from "react-icons/fa";
import { FaChevronUp } from "react-icons/fa";
import { Thumbnail } from 'react-bootstrap';
import commentBox from 'commentbox.io';
import PageWithComments from './comments';
import { connect } from 'react-redux';
import {FaRegBookmark} from 'react-icons/fa';
import { Link } from 'react-router-dom';


class Detailed extends Component{
    state= {
        id : "",
        toggleMenuDown : 'flex',
        toggleMenuUp : 'none',
        articleDetails: null,
        articleAvailable: false,
        bookmarkset : false
    }
// Toggle for News Description
    handleToggle = () =>{
        let down = this.state.toggleMenuUp 
        let up = this.state.toggleMenuDown 
        this.setState({
            toggleMenuDown : down,
            toggleMenuUp : up
        })
    }
// Getting the article Data

    componentWillMount(){
        this.setState({
            id:this.props.location.state.ArticleId
        })
        if(this.props.whichNews === 'Guardian' || this.props.whichNews === 'guardian'){
        let url = 'http://localhost:5000/article/' + this.props.location.state.ArticleId
        axios.get(url)
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    articleDetails : responseData.data,
                    articleAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
        }
        else if(this.props.whichNews === 'nytimes'){
        let url = 'http://localhost:5000/nyarticle/' + this.props.location.state.ArticleId
        axios.get(url)
            .then(responseData => {
                console.log("NY ARTICLE",responseData.data);
                this.setState({
                    articleDetails : responseData.data,
                    articleAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })

        }
    }

// Handling Bookmark

    handleBookmark = (e,title, image, news, date) =>{
        let localdata = JSON.parse(localStorage.getItem('favoriteData')) 
        if(!this.state.bookmarkset){
        console.log("set?", this.state.bookmarkset)
        console.log("Handling bookmark", title, image, news, date)
        let favoriteTitle = title.articleTitle
        let favoriteImage = image.articleImage
        let favoriteDate = date.articleDate
        let favoriteNews = news
        let favoriteId = this.state.id
        console.log(favoriteTitle,favoriteImage,favoriteDate,favoriteNews, favoriteId )
        localStorage.setItem('favoriteData',JSON.stringify([{title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews, id:favoriteId},{title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews, id:favoriteId},{title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews, id:favoriteId},{title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews, id:favoriteId},{title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews, id:favoriteId}]))
        this.setState({
            bookmarkset : !this.state.bookmarkset
        })
    }
    else{
        // let filteredList = th
        console.log("set?else", this.state.bookmarkset)
        localStorage.removeItem('favoriteData')
        this.setState({
            bookmarkset : !this.state.bookmarkset
        })

    }
}
    render(){
        if (!this.state.articleAvailable){
            return <div>Loading...</div>
        }
        else{
            if(this.props.whichNews === 'Guardian' || this.props.whichNews === 'guardian'){
            const articleObj = this.state.articleDetails
            const articleTitle = articleObj.webTitle
            const articleImage = articleObj.blocks.main.elements[0].assets[articleObj.blocks.main.elements[0].assets.length-1].file
            let articleDate = articleObj.webPublicationDate
            articleDate = articleDate.slice(0,10)
            const articleUrl = articleObj.webUrl
            const articleDescription = articleObj.blocks.body[0].bodyTextSummary
            const Description = articleDescription.match(/([^\.!\?]+[\.!\?]+)|([^\.!\?]+$)/g);
            {console.log(Description)}
            // {console.log("article",articleTitle,articleImage,articleDate,articleDescription)}
            if(Description.length<=4){
            return(
                <>
                    <Col lg={12} sm={12}>
                            <Card className="SearchCard" style={{borderRadius:'0', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)'}}>
                            <Card.Body>
                                <Card.Title><em style={{fontWeight:'initial'}}>{articleTitle}</em>
                                    <Card.Text className="mt-2">
                                        <span><em style={{fontWeight:'initial'}}> {articleDate}</em></span>
                                        <span className="justify-content-end float-right" style={{position:'relative', right:'10%'}}>
                                        <FacebookShareButton url={articleUrl} hashtag='CSCI_571_NewsApp' className="share">
                                            <FacebookIcon size={40} round={true}/>
                                        </FacebookShareButton>
                                        <TwitterShareButton url={articleUrl} hashtags={['CSCI_571_NewsApp']}>
                                            <TwitterIcon size={40} round={true}/>
                                        </TwitterShareButton>
                                        <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                            <EmailIcon size={40} round={true}/>
                                        </EmailShareButton>
                                        </span>
                                        <Button className="justify-content-end" style={{position:'relative', left:'88%', backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button>
                                    </Card.Text>
                                </Card.Title>
                                <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                <Accordion>
                                    <Card style={{border:'none'}}>
                                        <Card.Body style={{padding:'0px'}}>{articleDescription}</Card.Body>
                                    </Card>
                                </Accordion>
                            </Card.Body>
                            </Card >
                    </Col>
                    <br></br>
                    <PageWithComments id={this.state.id}/>
                </>
            )
            }
            // ==========================================================================================================
            else{
                // let card = []
                let i = 0
                let desc_visible = ""
                for(i=0;i<4;i++){
                    desc_visible += Description[i]
                }
                let desc_hidden = ""
                for(i=4;i<Description.length;i++){
                    desc_hidden += Description[i]
                }
                            return(
                                <>
                                <Col lg={12} sm={12}>
                                <Card className="SearchCard" style={{borderRadius:'0',boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)'}}>
                                <Card.Body>
                                    <Card.Title><em style={{fontWeight:'initial'}}>{articleTitle}</em>
                                        <Card.Text className="mt-2">
                                            <span><em style={{fontWeight:'initial'}}>{articleDate}</em></span>
                                            <span className="justify-content-end float-right" style={{position:'relative', right:'10%'}}>
                                                <FacebookShareButton url={articleUrl} hashtag='CSCI_571_NewsApp' className="share">
                                                    <FacebookIcon size={30} round={true}/>
                                                </FacebookShareButton>
                                                <TwitterShareButton url={articleUrl} hashtags={['CSCI_571_NewsApp']}>
                                                    <TwitterIcon size={30} round={true}/>
                                                </TwitterShareButton>
                                                <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                                    <EmailIcon size={30} round={true}/>
                                                </EmailShareButton>
                                            </span>
                                           <Button onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'guardian',{articleDate})} className="justify-content-end" style={{position:'relative', left:'88%', backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button>
                                        </Card.Text>
                                    </Card.Title>
                                    <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                    <Accordion>
                                        <Card style={{border:'none'}}>
                                            <Card.Body style={{padding:'0px'}}>{desc_visible}</Card.Body>
                                            <Accordion.Collapse eventKey="0">
                                                <Card.Body style={{padding:'0px'}}>{desc_hidden}</Card.Body>
                                            </Accordion.Collapse>
                                            <Card.Header style={{backgroundColor:'white'}}>
                                                <Accordion.Toggle eventKey="0" onClick={()=>{this.handleToggle()}} className="float-right" style={{backgroundColor:'white', border:'none'}}>
                                                    <span style={{display:this.state.toggleMenuDown}}><FaChevronDown/></span>
                                                    <span style={{display:this.state.toggleMenuUp}}><FaChevronUp/></span>
                                                </Accordion.Toggle>
                                            </Card.Header>
                                        </Card>
                                    </Accordion>
                                </Card.Body>
                                </Card >
                            </Col>
                            <br></br>
                            <PageWithComments id={this.state.id} />
                            </>
                            )
            }
        }



        else if(this.props.whichNews == 'nytimes'){
            let articleObj = this.state.articleDetails[0]
            // if(articleObj.hasOwnProperty('multimedia') && articleObj.hasOwnProperty('headline') && articleObj.multimedia){
                let articleTitle =  articleObj.hasOwnProperty('multimedia') && articleObj.hasOwnProperty('headline') && articleObj.multimedia? articleObj.headline.main : null
                let articleImage = (articleObj.hasOwnProperty('multimedia') && articleObj.hasOwnProperty('headline') && articleObj.multimedia.length!=0)? articleObj.multimedia[0].url : null
                // articleTitle = articleObj.headline.main
                articleImage = 'https://static01.nyt.com/' + articleImage
                // articleImage = articleObj.multimedia[0].url
                console.log("Detailed Article Image URL", articleImage)
                let articleDate = articleObj.pub_date
                articleDate = articleDate.slice(0,10)
                let articleUrl = articleObj.web_url
                let articleDescription = articleObj.abstract
                let Description = articleDescription.match(/([^\.!\?]+[\.!\?]+)|([^\.!\?]+$)/g);
                {console.log(Description)}
                console.log("article",articleTitle,articleImage,articleDate,articleDescription)
                if(Description.length<=4){
                    console.log("Less than 4")
                return(
                    <>
                        <Col lg={12} sm={12}>
                                <Card className="SearchCard" style={{borderRadius:'0', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)'}}>
                                <Card.Body>
                                    <Card.Title><em style={{fontWeight:'initial'}}>{articleTitle}</em>
                                        <Card.Text className="mt-2">
                                            <span><em style={{fontWeight:'initial'}}> {articleDate}</em></span>
                                            <span className="justify-content-end float-right" style={{position:'relative', right:'10%'}}>
                                            <FacebookShareButton url={articleUrl} hashtag='CSCI_571_NewsApp' className="share">
                                                <FacebookIcon size={40} round={true}/>
                                            </FacebookShareButton>
                                            <TwitterShareButton url={articleUrl} hashtags={['CSCI_571_NewsApp']}>
                                                <TwitterIcon size={40} round={true}/>
                                            </TwitterShareButton>
                                            <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                                <EmailIcon size={40} round={true}/>
                                            </EmailShareButton>
                                            </span>
                                        </Card.Text>
                                    </Card.Title>
                                    <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                    <Accordion>
                                        <Card style={{border:'none'}}>
                                            <Card.Body style={{padding:'0px'}}>{articleDescription}</Card.Body>
                                        </Card>
                                    </Accordion>
                                </Card.Body>
                                </Card >
                        </Col>
                        <br></br>
                        <PageWithComments id={this.state.id}/>
                    </>
                )
        }
    // }
            // ==========================================================================================================
            else{
                console.log("Less than 4")
                // let card = []
                console.log("greater than 4")
                let i = 0
                let desc_visible = ""
                for(i=0;i<4;i++){
                    desc_visible += Description[i]
                }
                let desc_hidden = ""
                for(i=4;i<Description.length;i++){
                    desc_hidden += Description[i]
                }
                            return(
                                <>
                                <Col lg={12} sm={12}>
                                <Card className="SearchCard" style={{borderRadius:'0',boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)'}}>
                                <Card.Body>
                                    <Card.Title><em style={{fontWeight:'initial'}}>{articleTitle}</em>
                                        <Card.Text className="mt-2">
                                            <span><em style={{fontWeight:'initial'}}>{articleDate}</em></span>
                                            <span className="justify-content-end float-right" style={{position:'relative', right:'10%'}}>
                                                <FacebookShareButton url={articleUrl} hashtag='CSCI_571_NewsApp' className="share">
                                                    <FacebookIcon size={30} round={true}/>
                                                </FacebookShareButton>
                                                <TwitterShareButton url={articleUrl} hashtags={['CSCI_571_NewsApp']}>
                                                    <TwitterIcon size={30} round={true}/>
                                                </TwitterShareButton>
                                                <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                                    <EmailIcon size={30} round={true}/>
                                                </EmailShareButton>
                                            </span>
                                        </Card.Text>
                                    </Card.Title>
                                    <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                    <Accordion>
                                        <Card style={{border:'none'}}>
                                            <Card.Body style={{padding:'0px'}}>{desc_visible}</Card.Body>
                                            <Accordion.Collapse eventKey="0">
                                                <Card.Body style={{padding:'0px'}}>{desc_hidden}</Card.Body>
                                            </Accordion.Collapse>
                                            <Card.Header style={{backgroundColor:'white'}}>
                                                <Accordion.Toggle eventKey="0" onClick={()=>{this.handleToggle()}} className="float-right" style={{backgroundColor:'white', border:'none'}}>
                                                    <span style={{display:this.state.toggleMenuDown}}><FaChevronDown/></span>
                                                    <span style={{display:this.state.toggleMenuUp}}><FaChevronUp/></span>
                                                </Accordion.Toggle>
                                            </Card.Header>
                                        </Card>
                                    </Accordion>
                                </Card.Body>
                                </Card >
                            </Col>
                            <br></br>
                            <PageWithComments id={this.state.id} />
                            </>
                        )
            }

        }
    }
}
}
// }
// }

const mapStateToProps = (state) =>{
    return{
        whichNews : state.newsState
    }
}


export default connect(mapStateToProps)(Detailed);
