import React, { Component, useState, useEffect } from 'react'
import {Card, Col, Row, Badge, ModalBody, ModalTitle, Button, Modal, ModalDialog} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './home.css';
import axios from 'axios';
import { FaShareAlt } from "react-icons/fa";
import {FacebookShareButton, TwitterShareButton, EmailShareButton} from 'react-share';
import {EmailIcon,FacebookIcon,TwitterIcon} from 'react-share';
import { connect } from 'react-redux';

const World = props =>{

    const [newsAvailable, changeNewsState] = useState(false)
    const [worldNews, getWorldNews] = useState([])
    const [isopenModal, changeModalState] = useState(false)
    const [activeItem, getActiveItem] = useState("")

    const fetchData=()=>{
        if(this.props.whichNews === 'Guardian' || this.props.whichNews === 'guardian'){
        axios.get('http://localhost:5000/guardian/world')
        .then(responseData => {
            console.log(responseData.data);
            getWorldNews(responseData.data)
            changeNewsState(true)
        })
        .catch(responseData=>{
            console.log("error");
        })
    }
    else if(this.props.whichNews === 'nytimes'){

    }
}



    useEffect(()=>{
        fetchData()
    }, [props.whichNews]);

    const handleClose = () =>{
        changeModalState(false)
    }

    const handleOpen = (title, weburl) =>{
        let sharedata = {title:title,webUrl:weburl}
        console.log(title)
        getActiveItem(sharedata)
        changeModalState(true)
    }

            console.log("World", props.whichNews)
            if (!newsAvailable){
                return <div>Loading...</div>
            }
            else{
            const newsCards = worldNews.map(news => {
                        let color = '#7c4eff'  
                        if(news.blocks.hasOwnProperty('main') && !news.blocks.main.elements[0].assets.length == 0){
                            console.log("Not present", news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file)
                        console.log("Data")
                        const newsTitle = news.webTitle;
                        const newsDescription = news.blocks.body[0].bodyTextSummary;
                        const newsImage = news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file;
                        console.log(newsImage)
                        const newsDate = news.webPublicationDate;
                        const newsBadge = news.sectionId;
                        const newsId = news.id;
                        const newsShareUrl = news.webUrl;
                        return (
                            <Link to={{ pathname: '/DetailedArticle/' + newsId ,state: {ArticleId: newsId}}} style={{ width:'100%', display:'block', textDecoration: 'none', color:'inherit' }}>
                            <div className="HomepageCard">
                            <Card style={{ width: '100%', marginTop:'30px', marginBottom:'30px', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)' }} >
                                    <Row>
                                        <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                                            <Card.Img className="img-responsive" variant="top" src={newsImage} style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                                        </Col>
                                        <Col lg={9} xs={12} className="mr-0 ml-0">
                                            <Card.Body>
                                                <Card.Title><em>{newsTitle}<Link style={{zIndex:'1', position:'relative', textDecoration: 'none', color:'inherit'}} onClick={()=>handleOpen(newsTitle, newsShareUrl)}><FaShareAlt/></Link></em></Card.Title>
                                                <Card.Text className="news-description">
                                                    {newsDescription}
                                                </Card.Text>
                                                <Card.Text>
                                                <span> {newsDate} </span>
                                                <Badge style={{backgroundColor: color, color:'white'}} className="float-right">
                                                    {newsBadge}
                                                </Badge>
                                                </Card.Text>
                                            </Card.Body>
                                        </Col>
                                    </Row>
                                </Card>
                            </div>
                            </Link>
                            )
                        }
                    })
            console.log(newsCards)
            return (
            <>
                {newsCards}
                <Modal show={isopenModal} onHide={()=>handleClose}>
                        <Modal.Header closeButton>
                            <Modal.Title>{activeItem.title}</Modal.Title>
                        </Modal.Header>

                        <Modal.Body style={{marginTop:'1px',marginBottom:'1px', paddingBottom:'0px', fontSize:'20px'}} className="d-flex justify-content-center"><b>Share via</b></Modal.Body>
                        <Modal.Body className="d-flex justify-content-between">
                        <FacebookShareButton url={activeItem.webUrl} hashtag='CSCI_571_NewsApp' className="share">
                            <FacebookIcon size={70} className='float-left' round={true}/>
                        </FacebookShareButton>
                        <TwitterShareButton url={activeItem.webUrl} hashtags={['CSCI_571_NewsApp']}>
                            <TwitterIcon size={70} round={true}/>
                        </TwitterShareButton>
                        <EmailShareButton url={activeItem.webUrl} subject='#CSCI_571_NewsApp'>
                            <EmailIcon size={70} className='float-right' round={true}/>
                        </EmailShareButton>
                        </Modal.Body>
                </Modal>
            </>
            );
        }
}


const mapStateToProps = (state) =>{
    return{
        whichNews : state.newsState
    }
}



export default connect(mapStateToProps)(World);
