import React, { Component } from 'react'
import {Card, Col, Row, Badge, Button, Accordion} from 'react-bootstrap';
import './detailed_page.css';
import axios from 'axios';
import {FacebookShareButton, TwitterShareButton, EmailShareButton} from 'react-share';
import {EmailIcon,FacebookIcon,TwitterIcon} from 'react-share';
import { FaChevronDown } from "react-icons/fa";
import { FaChevronUp } from "react-icons/fa";
import { Thumbnail } from 'react-bootstrap';


class Detailed extends Component{
    state= {
        id : "",
        toggleMenuDown : 'flex',
        toggleMenuUp : 'none',
        articleDetails: null,
        articleAvailable: false
    }

    handleToggle = () =>{
        let down = this.state.toggleMenuUp 
        let up = this.state.toggleMenuDown 
        this.setState({
            toggleMenuDown : down,
            toggleMenuUp : up
        })
    }

    componentWillMount(){
        this.setState({
            id:this.props.location.state.ArticleId
        })
        let url = 'http://localhost:5000/article/' + this.props.location.state.ArticleId
        axios.get(url)
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    articleDetails : responseData.data,
                    articleAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
    }
    render(){
        if (!this.state.articleAvailable){
            return <div>Loading...</div>
        }
        else{
            // if(this.state.articleDetails.blocks.hasOwnProperty('main') && !this.state.articleDetails.blocks.main.elements[0].assets.length == 0){
            const articleObj = this.state.articleDetails
            const articleTitle = articleObj.webTitle
            const articleImage = articleObj.blocks.main.elements[0].assets[articleObj.blocks.main.elements[0].assets.length-1].file
            const articleDate = articleObj.webPublicationDate
            const articleDescription = articleObj.blocks.body[0].bodyTextSummary
            const Description = articleDescription.match(/([^\.!\?]+[\.!\?]+)|([^\.!\?]+$)/g);
            {console.log(Description)}
            // {console.log("article",articleTitle,articleImage,articleDate,articleDescription)}
            if(Description.length<=4){
                let card = `<Col lg={12} sm={12}>
                            <Card className="SearchCard">
                            <Card.Body>
                                <Card.Title><em>${articleTitle}</em>
                                    <Card.Text className="mt-2">
                                        <span> ${articleDate} </span>
                                        <Badge variant="primary" className="float-right">
                                            Bookmark
                                        </Badge>
                                    </Card.Text>
                                </Card.Title>
                                <Card.Img variant="top" src=${articleImage} className="DetailedCardImg"/>
                                <Accordion>
                                    <Card style={{border:'none'}}>
                                        <Card.Body>${articleDescription}</Card.Body>
                                        // <Accordion.Collapse eventKey="0">
                                        //     <Card.Body>Each of the Card components in the Accordion can have their entire header clickable, by setting the AccordionToggle's underlying component to be a CardHeader component.Each of the Card components in the Accordion can have their entire header clickable, by setting the AccordionToggle's underlying component to be a CardHeader component.Each of the Card components in the Accordion can have their entire header clickable, by setting the AccordionToggle's underlying component to be a CardHeader component.Each of the Card components in the Accordion can have their entire header clickable, by setting the AccordionToggle's underlying component to be a CardHeader component.</Card.Body>
                                        // </Accordion.Collapse>
                                        // <Card.Header style={{backgroundColor:'white'}}>
                                        //     <Accordion.Toggle eventKey="0" onClick={()=>{this.handleToggle()}} className="float-right" style={{backgroundColor:'white', border:'none'}}>
                                        //         <span style={{display:this.state.toggleMenuDown}}><FaChevronDown/></span>
                                        //         <span style={{display:this.state.toggleMenuUp}}><FaChevronUp/></span>
                                        //     </Accordion.Toggle>
                                        // </Card.Header>
                                    </Card>
                                </Accordion>
                            </Card.Body>
                            </Card >
            </Col>`
            return(
                <>
                    {card}
                </>
            )
            }
            // ==========================================================================================================
            else{
                // let card = []
                let i = 0
                let desc_visible = ""
                for(i=0;i<4;i++){
                    desc_visible += Description[i]
                }
                let desc_hidden = ""
                for(i=4;i<Description.length;i++){
                    desc_hidden += Description[i]
                }
                            return(
                                <>
                                <Col lg={12} sm={12}>
                                <Card className="SearchCard">
                                <Card.Body>
                                    <Card.Title><em>{articleTitle}</em>
                                        <Card.Text className="mt-2">
                                            <span> {articleDate} </span>
                                            <Badge variant="primary" className="float-right">
                                                Bookmark
                                            </Badge>
                                        </Card.Text>
                                    </Card.Title>
                                    <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                    <Accordion>
                                        <Card style={{border:'none'}}>
                                            <Card.Body>{desc_visible}</Card.Body>
                                            <Accordion.Collapse eventKey="0">
                                                <Card.Body>{desc_hidden}</Card.Body>
                                            </Accordion.Collapse>
                                            <Card.Header style={{backgroundColor:'white'}}>
                                                <Accordion.Toggle eventKey="0" onClick={()=>{this.handleToggle()}} className="float-right" style={{backgroundColor:'white', border:'none'}}>
                                                    <span style={{display:this.state.toggleMenuDown}}><FaChevronDown/></span>
                                                    <span style={{display:this.state.toggleMenuUp}}><FaChevronUp/></span>
                                                </Accordion.Toggle>
                                            </Card.Header>
                                        </Card>
                                    </Accordion>
                                </Card.Body>
                                </Card >
                            </Col>
                            </>
                            )
            }
            // }
// Here
        }
    }
}

export default Detailed;
