import React from 'react';
import CustomNavbar from './components/Customnavbar';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import Home from './components/home';
import World from './components/world';
import Politics from './components/politics';
import Business from './components/business';
import Technology from './components/technology';
import Sports from './components/sports';
import SearchResults from './components/searchResults';
import Detailed from './components/detailed_page';
import Favorites from './components/favorites';
import axios from 'axios';
import { connect } from 'react-redux';


class App extends React.Component{
  state= {
    news:"guardian"
  }
  changeNews = (news) =>{
    this.setState({news})
  }
  componentDidMount(){
    localStorage.setItem('favoriteData',[])
    console.log("Setting local storage")
    console.log("Fetching News data")
    axios.get('http://localhost:5000/getNewsdata')
        .then(responseData => {
          console.log("Data from the file",responseData.data)
          this.setState({
            news:responseData.data
          })
        })
        .catch(responseData=>{
            console.log("error from here");
        })
  }

  render(){

    return (
          <Router>
            <div className="App" style={{overflowX:'hidden'}}>
            <CustomNavbar newsChange={this.changeNews}/>
            <Switch>
              <Route exact path="/" component={Home}/>
              <Route path="/world" component={World}/>
              <Route path="/politics" component={Politics}/>
              <Route path="/business" component={Business}/>
              <Route path="/technology" component={Technology}/>
              <Route path="/sports" component={Sports}/>
              <Route path="/search/:option" component={SearchResults}/>
              <Route path="/DetailedArticle/:news/:articleid" component={Detailed}/>
              {/* <Route path="/DetailedArticle/:articleid" component={Detailed}/> */}
              <Route path="/favorites" component={Favorites}/>
            </Switch>
            </div>
          </Router>

    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return{
  changeNewsState : (newsValue) => { dispatch({type: 'CHANGE_NEWS', newsValue: newsValue})}
  }
}

export default connect(mapDispatchToProps)(App);
