import React, { Component } from 'react'
import {Card, Col, Row, Badge} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './home.css';
import axios from 'axios';
import { FaShareAlt } from "react-icons/fa";

class Politics extends Component{
    state= {
        politicsNews: [],
        newsAvailable: false
    }
    componentWillMount(){
        axios.get('http://localhost:5000/guardian/politics')
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    politicsNews : responseData.data,
                    newsAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
    }
        render(){
            if (!this.state.newsAvailable){
                return <div>Loading...</div>
            }
            else{
            console.log("length", this.state.politicsNews.length)
            const newsObjs = this.state.politicsNews
            const newsCards = this.state.politicsNews.map(news => {
                        if(news.sectionId === 'sport'){
                            let badgeVariant = "warning";
                        }
                        else if(news.sectionId === 'world'){
                            let badgeVariant = "primary";
                        }
                        else if(news.sectionId === 'technology'){
                            let badgeVariant = "success";
                        }
                        else if(news.sectionId === 'politics'){
                            let badgeVariant = "info";
                        }
                        else if(news.sectionId === 'business'){
                            let badgeVariant = "primary";
                        }
                        else {
                            let badgeVariant = "secondary";
                        }
                        if(news.blocks.hasOwnProperty('main') && !news.blocks.main.elements[0].assets.length == 0){
                        let newsTitle = news.webTitle;
                        let newsDescription = news.blocks.body[0].bodyTextSummary;
                        let newsImage = news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file;
                        let newsDate = news.webPublicationDate;
                        let newsBadge = news.sectionId;
                        console.log("Messages",newsImage)
                        if(newsImage != "Cannot read property 'file' of undefined"){
                        console.log("IN")
                        console.log("Messages",newsImage)
                        return (
                            <Link to='/world' style={{ position:'relative', zInedx:'-1',width:'100%', display:'block', textDecoration: 'none', color:'inherit' }}>
                            <div className="HomepageCard">
                            <Card style={{ width: '100%', marginTop:'30px', marginBottom:'30px', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)' }} >
                                    <Row>
                                        <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                                            <Card.Img className="img-responsive" variant="top" src={newsImage} style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                                        </Col>
                                        <Col lg={9} xs={12} className="mr-0 ml-0">
                                            <Card.Body>
                                                <Card.Title><em>{newsTitle}<FaShareAlt/><a href="http://www.google.com">Click Me</a></em></Card.Title>
                                                <Card.Text className="news-description">
                                                    {newsDescription}
                                                </Card.Text>
                                                <Card.Text>
                                                <span> {newsDate} </span>
                                                <Badge variant="primary" className="float-right">
                                                    {newsBadge}
                                                </Badge>
                                                </Card.Text>
                                            </Card.Body>
                                        </Col>
                                    </Row>
                                </Card>
                            </div>
                            </Link>
                            )}
                        }
                    })
            console.log(newsCards)
            return (
            <>
                {newsCards}
            </>
            );
        }
    }
}

export default Politics;
