import React, { useState, useEffect, Component } from 'react'
import {Card, Col, Row, Badge} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './search.css';
import {FaBookmark} from 'react-icons';



class Favorites extends Component{
    state = {
        favorites : [
            {title:"Bleh",image:"https://media.guim.co.uk/b49dcc4897f479fb8e72c72b2f2ca80c7a400e84/0_69_3000_1800/master/3000.jpg",date:"2020-03-24",news:"guardian"}
        ]
    }

    componentDidMount(){
        const favoriteData = localStorage.getItem('favoriteData')
        let data = JSON.parse(favoriteData)
        let favData = [...this.state.favorites, data]
        this.setState({
            favorites : favData
        })
    }

    render(){
        if(this.state.favorites.length!=0){
        let favoriteList = this.state.favorites.map(favorite =>{
                        let favoriteTitle = favorite.title
                        let favoriteImage = favorite.image
                        let favoriteDate =  favorite.date
                        return (
                            <Col lg={3} sm={12}>
                                <Card className="SearchCard">
                                <Card.Body>
                                    <Card.Title>{favoriteTitle}</Card.Title>
                                    <Card.Img variant="top" src={favoriteImage} className="SearchCardImg"/>
                                    <Card.Text className="mt-2">
                                        <span> {favoriteDate} </span>
                                        <Badge variant="primary" className="float-right">
                                            news
                                        </Badge>
                                    </Card.Text>
                                </Card.Body>
                                </Card >
                            </Col>
                        )
                    }
                )
        return(
        <>
            <div style={{marginLeft:'20px', marginRight:'20px'}}>
                <h2 style={{marginTop:'3px', marginBottom:'0px'}}>Favorites</h2>
                <Row>
                    {favoriteList}
                </Row>
            </div>
        </>

        )
      }
      else{
        return(
            <>
                <div>Loading ..</div>
            </>
            )
      }
    }
}
export default Favorites;

