import React, { Component } from 'react'
// import {Card, Col, Row, Badge} from 'react-bootstrap';
// import { Link } from 'react-router-dom';
import './search.css';
import axios from 'axios';

class searchResults extends Component{
    state= {
        searchResults: [],
        newsAvailable: false
    }
    componentWillMount(){
        let url = 'http://localhost:5000/searchResults/' + this.props.location.state.searchWord
        axios.get(url)
        .then(responseData => {
            console.log(responseData.data);})
            this.setState({
                worldNews : responseData.data,
                newsAvailable : true
            })
        .catch(responseData=>{
            console.log("ERROR from Search Results");
        })
    }
    render(){
        return(
            <div>{this.props.location.state.searchWord}</div>
        )
}
}

export default searchResults;
