import React from 'react';
import commentBox from 'commentbox.io';
import './home.css'

class PageWithComments extends React.Component {
    state = {
        id_value:""
    }
    componentDidMount() {
        this.setState({
            id_value:this.props.id
        })
        this.removeCommentBox = commentBox('5725357550338048-proj');
    }

    componentWillUnmount() {

        this.removeCommentBox();
    }

    render() {

        return (
            <div className="commentbox" id={this.state.id_value}/>
        );
    }
}

export default PageWithComments




