import React, { useState, useEffect } from 'react'
import {Card, Col, Row, Badge} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './search.css';
import axios from 'axios';
import {FaBookmark} from 'react-icons';
import BounceLoader from "react-spinners/BounceLoader";

const SearchResults = props => {
    const [searchResults, setResults] = useState([])
    const [newsAvailable, setNewsAvailable] = useState(false) 

    const fetchData = () => {
        let url = 'http://localhost:5000/searchResults/' + props.location.state.searchWord
        axios.get(url)
        .then(responseData => {
            console.log(responseData.data);
            setResults(responseData.data)
            setNewsAvailable(true)

        })
        .catch(responseData=>{
            console.log("ERROR from Search Results");
            })

    }

    useEffect(() => {
        fetchData()
    },[props.location.state.searchWord])

        if (!newsAvailable){
            return(
                <div className="sweet-loading" style = {{position:'fixed', top:'50%', left:'47%'}}>
                        <BounceLoader
                        style = {{display:'block', background:'#2b4fc4'}}
                        size={35}
                        color={"#2b4fc4"}
                        />
                    <span style={{position:'fixed',left:'46.5%', fontSize:'14px'}}>Loading</span>
                </div>
            )
        }
        else{
        const newsCards = searchResults.map(news => {
                    let color = ""
                    let text_color = "white"
                    if(news.sectionId == 'sport'){
                        color = '#f6c244';
                        text_color = 'black';
                    }
                    else if(news.sectionId == 'world'){
                        color = '#7c4eff';
                    }
                    else if(news.sectionId == 'technology'){
                        color = '#cedc39';
                        text_color = 'black';
                    }
                    else if(news.sectionId == 'politics'){
                        color = '#419488';
                    }
                    else if(news.sectionId == 'business'){
                        color = '#4696ec';
                    }
                    else {
                        color = '#6e757c';
                    }
                    if(!news.blocks.main.elements[0].assets.length == 0){
                    const newsTitle = news.webTitle;
                    const newsDescription = news.blocks.body[0].bodyTextSummary;
                    const newsImage = news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file;
                    console.log(newsImage)
                    const newsDate = news.webPublicationDate;
                    const newsBadge = news.sectionId;
                    return (
                        <Col lg={3} sm={12}>
                            <Card className="SearchCard">
                            <Card.Body>
                                <Card.Title>{newsTitle}</Card.Title>
                                <Card.Img variant="top" src={newsImage} className="SearchCardImg"/>
                                <Card.Text className="mt-2">
                                    <span> {newsDate} </span>
                                    <Badge style={{color:text_color, backgroundColor:color}} className="float-right">
                                        {newsBadge}
                                    </Badge>
                                </Card.Text>
                            </Card.Body>
                            </Card >
                        </Col>
                        )
                    }
                })
        return (
        <>
        <div style={{marginLeft:'20px', marginRight:'20px'}}>
                <h2 style={{marginTop:'3px', marginBottom:'0px'}}>Results</h2>
                <Row>
                    {newsCards}
                </Row>
        </div>
        </>
        );
    }

}

export default SearchResults;














