import { Thumbnail } from 'react-bootstrap';
import commentBox from 'commentbox.io';
import PageWithComments from './comments';
import { connect } from 'react-redux';
import {FaRegBookmark} from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { ToastContainer,toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'


class Detailed extends Component{
    state= {
        id : "",
        toggleMenuDown : 'flex',
        toggleMenuUp : 'none',
        articleDetails: null,
        articleAvailable: false,
        bookmarkset : false
    }
// Toggle for News Description
    handleToggle = () =>{
        let down = this.state.toggleMenuUp 
        let up = this.state.toggleMenuDown 
        this.setState({
            toggleMenuDown : down,
            toggleMenuUp : up
        })
    }
// Getting the article Data

    componentWillMount(){
        this.setState({
            id:this.props.location.state.ArticleId
        })
        if(this.props.whichNews === 'Guardian' || this.props.whichNews === 'guardian'){
        let url = 'http://localhost:5000/article/' + this.props.location.state.ArticleId
        axios.get(url)
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    articleDetails : responseData.data,
                    articleAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
        }
        else if(this.props.whichNews === 'nytimes'){
        let url = 'http://localhost:5000/nyarticle/' + this.props.location.state.ArticleId
        axios.get(url)
            .then(responseData => {
                console.log("NY ARTICLE",responseData.data);
                this.setState({
                    articleDetails : responseData.data,
                    articleAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })

        }
    }

// Handling Bookmark

    handleBookmark = (e,title, image, news, date, section) =>{
        let localdataFetch = localStorage.getItem('favoriteData')
        let localdata = localdataFetch?JSON.parse(localdataFetch):[]
        if(localdata.length != 0){
        let fetchArticle = localdata.filter(f_article =>{return f_article.id === this.props.location.state.ArticleId})
        console.log("Article Fetched",fetchArticle)
        let alreadyThere = fetchArticle.length>0?true:false
        console.log("There",alreadyThere)

        if(alreadyThere){
            // Remove it
            let data = localdata.filter(f_article =>{
                console.log("f_article",f_article.id)
                console.log('Article ID', this.props.location.state.ArticleId)
                return f_article.id != this.props.location.state.ArticleId})
            console.log("Already Present-So removing", data)
            localStorage.setItem('favoriteData', JSON.stringify(data))

        }
        else{
            toast("Wow so easy !")
            let favoriteTitle = title.articleTitle
            let favoriteImage = image.articleImage
            let favoriteDate = date.articleDate
            let favoriteNews = news
            let favoriteId = this.state.id
            let favoriteSection = section.articleSection
            // Add article
            let data = [...localdata, {title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews,id:favoriteId,section:favoriteSection}]
            console.log("Not There- So adding",data)
            localStorage.setItem('favoriteData', JSON.stringify(data))
        }

    }
    else{
        toast("Wow so easy !")
        let favoriteTitle = title.articleTitle
        let favoriteImage = image.articleImage
        let favoriteDate = date.articleDate
        let favoriteNews = news
        let favoriteId = this.state.id
        let favoriteSection = section.articleSection
        // Add article
        let data = [...localdata, {title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews,id:favoriteId,section:favoriteSection}]
        console.log("No data- So adding 1st Element", data)
        localStorage.setItem('favoriteData', JSON.stringify(data))

    }
}
