                                                <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                                    <EmailIcon size={30} round={true}/>
                                                </EmailShareButton>
                                            </span>
                                            <Button onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'NYTIMES',{articleDate},{articleSection})} className="justify-content-end" style={{position:'relative', left:'88%', backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button>
