let news = localStorage.getItem('restoringNews')
console.log("first reducer", news)
news = (news == null)?"guardian":news
console.log(news)
const initState = {
    newsState:news
}

const newsReducer = (state = initState, action) => {
    if(action.type == 'CHANGE_NEWS'){
        console.log("from store", action.newsValue)
        return{
            newsState : action.newsValue
        }
    }
    return state
}

export default newsReducer;
