import React, { Component } from 'react'
import {Card, Col, Row, Badge} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import logo from './logo512.png'
import './home.css';
import axios from 'axios';

class Home extends Component{
    state= {
        newsHeadlines: []
    }
    componentDidMount(){
        axios.get('http://localhost:5000/')
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    newsHeadlines : responseData.data
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
    }

    render(){
        // destructuring
        const { newsHeadlines } =  this.state;
        const newsObjs = newsHeadlines.length? (
            newsHeadlines.map(news => {
                const newsTitle = news.WebTitle;
                // const newsDescription = news.blocks.body.0.bodyTextSummary;
                const newsImage = news.blocks.main.elements;
                const newsSection = news.sectionId;
                return(
                <div>
                    <div>{newsTitle}</div>
                    <div>{newsImage}</div>
                    <div>{newsSection}</div>
                </div>
                )
            })
        ) : (<h1>No Headlines</h1>)

        console.log("hello",newsObjs);
        return(
            <>
            <Link to='/world' style={{ width:'100%', display:'block', textDecoration: 'none', color:'inherit' }}>
            <div className="HomepageCard">
               <Card style={{ width: '100%' }} >
                    <Row>
                        <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                            <Card.Img className="img-responsive" variant="top" src="https://csci571.com/hw/hw3/images/lake.jpg" style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                        </Col>
                        <Col lg={9} xs={12} className="mr-0 ml-0">
                            <Card.Body>
                                <Card.Title>Card Title</Card.Title>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of
                                    the card's content. Some quick example text to build on the card title and make up the bulk of
                                    the card's content.  Some quick example text to build on the card title and make up the bulk of
                                    the card's content. Some quick example text to build on the card title and make up the bulk of
                                    the card's content.Some quick example text to build on the card title and make up the bulk of
                                    the card's content.
                                </Card.Text>
                                <Card.Text>
                                <span> Date </span>
                                <Badge pill variant="primary" className="float-right">
                                    Primary
                                </Badge>
                                </Card.Text>
                            </Card.Body>
                        </Col>
                    </Row>
                </Card>
            </div>
            </Link>

            <Link to='/world' style={{ width:'100%', display:'block', textDecoration: 'none', color:'inherit' }}>
            <div className="HomepageCard">
            <Card style={{ width: '100%' }} >
                    <Row>
                        <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                            <Card.Img className="img-responsive" variant="top" src="https://csci571.com/hw/hw3/images/lake.jpg" style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                        </Col>
                        <Col lg={9} xs={12} className="mr-0 ml-0">
                            <Card.Body>
                                <Card.Title>Card Title</Card.Title>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of
                                    the card's content. Some quick example text to build on the card title and make up the bulk of
                                    the card's content.  Some quick example text to build on the card title and make up the bulk of
                                    the card's content. Some quick example text to build on the card title and make up the bulk of
                                    the card's content.Some quick example text to build on the card title and make up the bulk of
                                    the card's content.
                                </Card.Text>
                                <Card.Text>
                                <span> Date </span>
                                <Badge pill variant="primary" className="float-right">
                                    Primary
                                </Badge>
                                </Card.Text>
                            </Card.Body>
                        </Col>
                    </Row>
                </Card>
            </div>
            </Link>
            </>
        );
    }
}

export default Home;
