import React, { Component } from 'react'
import {Card, Col, Row, Badge, ModalBody, ModalTitle, Button, Modal, ModalDialog} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './home.css';
import axios from 'axios';
import { FaShareAlt } from "react-icons/fa";
import {FacebookShareButton, TwitterShareButton, EmailShareButton} from 'react-share';
import {EmailIcon,FacebookIcon,TwitterIcon} from 'react-share';
import { connect } from 'react-redux';

class Home extends Component{
    state= {
        worldNews: [],
        newsAvailable: false,
        isopenModal : false,
        activeItem: "",
        news:"guardian",
    }

    componentWillMount(){
        console.log("Home", this.props.whichNews)
        if(this.props.whichNews === 'Guardian' || this.props.whichNews === 'guardian'){
        axios.get('http://localhost:5000/guardian/world')
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    worldNews : responseData.data,
                    newsAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
        }
        else if(this.props.whichNews === 'nytimes'){
            axios.get('http://localhost:5000/nytimes/world')
            .then(responseData => {
                console.log("NY TIMES",responseData.data);
                this.setState({
                    worldNews : responseData.data,
                    newsAvailable : true,
                })
                console.log("NYT State data", this.state.worldNews)
            })
            .catch(responseData=>{
                console.log("error");
            })
        }
    }

    componentWillReceiveProps(nextProps){
        if( nextProps.whichNews != this.props.whichNews ) {
            console.log("hello")
            this.setState({
                changed:true,
                newsAvailable:false
            },()=>{
                console.log('hi',this.props.whichNews)
                if (this.props.whichNews === 'guardian'){
                    console.log("checked")
                            axios.get('http://localhost:5000/guardian/world')
                            .then(responseData => {
                                console.log("GUARDIAN",responseData.data);
                                this.setState({
                                    worldNews : responseData.data,
                                    newsAvailable : true,
                                    changed:false,
                                })
                            console.log("Guardian State data", this.state.worldNews)
                            })
                            .catch(responseData=>{
                                console.log("error");
                            })
                        }
                else if(this.props.whichNews === 'nytimes'){
                            axios.get('http://localhost:5000/nytimes/world')
                            .then(responseData => {
                                console.log("NY TIMES",responseData.data);
                                this.setState({
                                    worldNews : responseData.data,
                                    newsAvailable : true,
                                    changed:false
                                })
                                console.log("NYT State data", this.state.worldNews)
                            })
                            .catch(responseData=>{
                                console.log("error");
                            })
                        }
                    }

            )}}


    handleClose = () =>{
        this.setState({
            isopenModal : false
        })
    }

    handleOpen = (title, weburl) =>{
        let sharedata = {title:title,webUrl:weburl}
        console.log(title)
        this.setState({activeItem:sharedata})
        this.setState({ isopenModal: true });
    }

        render(){
            console.log("changed state")
            if (!this.state.newsAvailable){
                return <div>Loading...</div>
            }
            else{
            if(this.props.whichNews === 'guardian' || this.props.whichNews === 'Guardian'){
            const newsObjs = this.state.worldNews
            const newsCards = this.state.worldNews.map(news => {
                        let color = "brown"
                        let text_color = "white"
                        if(news.sectionId == 'sport'){
                            color = '#f6c244';
                            text_color = 'black';
                        }
                        else if(news.sectionId == 'world'){
                            color = '#7c4eff';
                        }
                        else if(news.sectionId == 'technology'){
                            color = '#cedc39';
                            text_color = 'black';
                        }
                        else if(news.sectionId == 'politics'){
                            color = '#419488';
                        }
                        else if(news.sectionId == 'business'){
                            color = '#4696ec';
                        }
                        else {
                            color = '#6e757c';
                        }

                        if(news.blocks.hasOwnProperty('main') && !news.blocks.main.elements[0].assets.length == 0){
                        const newsTitle = news.webTitle;
                        const newsDescription = news.blocks.body[0].bodyTextSummary;
                        const newsImage = news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file;
                        const newsDate = news.webPublicationDate;
                        const newsBadge = news.sectionId;
                        const newsId = news.id;
                        const newsShareUrl = news.webUrl;
                        // const pathname = '/DetailedArticle/' + newsId
                        return (
                            <Link to={{ pathname: '/DetailedArticle/' + newsId ,state: {ArticleId: newsId}}} style={{ width:'100%', display:'block', textDecoration: 'none', color:'inherit' }} key={newsId}>
                            <div className="HomepageCard" key={newsId}>
                            <Card style={{ width: '100%', marginTop:'30px', marginBottom:'30px', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)' }} >
                                    <Row>
                                        <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                                            <Card.Img className="img-responsive" variant="top" src={newsImage} style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                                        </Col>
                                        <Col lg={9} xs={12} className="mr-0 ml-0">
                                            <Card.Body>
                                                <Card.Title><em>{newsTitle}</em><Link style={{zIndex:'1', position:'relative', textDecoration: 'none', color:'inherit'}} onClick={()=>this.handleOpen(newsTitle, newsShareUrl)}><FaShareAlt/></Link></Card.Title>
                                                <Card.Text className="news-description">
                                                    {newsDescription}
                                                </Card.Text>
                                                <Card.Text>
                                                <span> {newsDate} </span>
                                                <Badge style={{backgroundColor: color, color:text_color}} className="float-right">
                                                    {newsBadge}
                                                </Badge>
                                                </Card.Text>
                                            </Card.Body>
                                        </Col>
                                    </Row>
                                </Card>
                            </div>
                            </Link>
                      )
                    }
                })
                console.log(newsCards)
            return (
            <>
                {newsCards}
                <Modal show={this.state.isopenModal} onHide={this.handleClose}>
                        <Modal.Header closeButton>
                            <Modal.Title>{this.state.activeItem.title}</Modal.Title>
                        </Modal.Header>

                        <Modal.Body style={{marginTop:'1px',marginBottom:'1px', paddingBottom:'0px', fontSize:'20px'}} className="d-flex justify-content-center"><b>Share via</b></Modal.Body>
                        <Modal.Body className="d-flex justify-content-between">
                        <FacebookShareButton url={this.state.activeItem.webUrl} hashtag='CSCI_571_NewsApp' className="share">
                            <FacebookIcon size={70} className='float-left' round={true}/>
                        </FacebookShareButton>
                        <TwitterShareButton url={this.state.activeItem.webUrl} hashtags={['CSCI_571_NewsApp']}>
                            <TwitterIcon size={70} round={true}/>
                        </TwitterShareButton>
                        <EmailShareButton url={this.state.activeItem.webUrl} subject='#CSCI_571_NewsApp'>
                            <EmailIcon size={70} className='float-right' round={true}/>
                        </EmailShareButton>
                        </Modal.Body>
                </Modal>
            </>
            );
          }
          else if(this.props.whichNews === 'nytimes'){
            const newsObjs = this.state.worldNews
            const newsCards = this.state.worldNews.map(news => {
                        let color = "brown"
                        let text_color = "white"
                        if(news.section == 'sport'){
                            color = '#f6c244';
                            text_color = 'black';
                        }
                        else if(news.section == 'world'){
                            color = '#7c4eff';
                        }
                        else if(news.section == 'technology'){
                            color = '#cedc39';
                            text_color = 'black';
                        }
                        else if(news.section == 'politics'){
                            color = '#419488';
                        }
                        else if(news.section == 'business'){
                            color = '#4696ec';
                        }
                        else {
                            color = '#6e757c';
                        }
                        let count = 0
                        if(news.hasOwnProperty('multimedia')){
                            if (news.multimedia){
                        const newsTitle = news.title;
                        const newsDescription = news.abstract;
                        let newsImage = news.multimedia[0].url
                        // console.log(news)
                        const newsDate = news.published_date;
                        const newsBadge = news.section;
                        const newsId = news.uri;
                        const newsShareUrl = news.url;
                        // const pathname = '/DetailedArticle/' + newsId
                        return (
                            <Link to={{ pathname: '/DetailedArticle/' + newsId ,state: {ArticleId: newsId}}} style={{ width:'100%', display:'block', textDecoration: 'none', color:'inherit' }} key={newsId}>
                            <div className="HomepageCard" key={newsId}>
                            <Card style={{ width: '100%', marginTop:'30px', marginBottom:'30px', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)' }} >
                                    <Row>
                                        <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                                            <Card.Img className="img-responsive" variant="top" src={newsImage} style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                                        </Col>
                                        <Col lg={9} xs={12} className="mr-0 ml-0">
                                            <Card.Body>
                                                <Card.Title><em>{newsTitle}</em><Link style={{zIndex:'1', position:'relative', textDecoration: 'none', color:'inherit'}} onClick={()=>this.handleOpen(newsTitle, newsShareUrl)}><FaShareAlt/></Link></Card.Title>
                                                <Card.Text className="news-description">
                                                    {newsDescription}
                                                </Card.Text>
                                                <Card.Text>
                                                <span> {newsDate} </span>
                                                <Badge style={{backgroundColor: color, color:text_color}} className="float-right">
                                                    {newsBadge}
                                                </Badge>
                                                </Card.Text>
                                            </Card.Body>
                                        </Col>
                                    </Row>
                                </Card>
                            </div>
                            </Link>
                      )
                    }
                }
                })
                console.log(newsCards)
            return (
            <>
                {newsCards}
                <Modal show={this.state.isopenModal} onHide={this.handleClose}>
                        <Modal.Header closeButton>
                            <Modal.Title>{this.state.activeItem.title}</Modal.Title>
                        </Modal.Header>
                        <Modal.Body style={{marginTop:'1px',marginBottom:'1px', paddingBottom:'0px', fontSize:'20px'}} className="d-flex justify-content-center"><b>Share via</b></Modal.Body>
                        <Modal.Body className="d-flex justify-content-between">
                        <FacebookShareButton url={this.state.activeItem.webUrl} hashtag='CSCI_571_NewsApp' className="share">
                            <FacebookIcon size={70} className='float-left' round={true}/>
                        </FacebookShareButton>
                        <TwitterShareButton url={this.state.activeItem.webUrl} hashtags={['CSCI_571_NewsApp']}>
                            <TwitterIcon size={70} round={true}/>
                        </TwitterShareButton>
                        <EmailShareButton url={this.state.activeItem.webUrl} subject='#CSCI_571_NewsApp'>
                            <EmailIcon size={70} className='float-right' round={true}/>
                        </EmailShareButton>
                        </Modal.Body>
                </Modal>
            </>
            );
            }
        }
    }
}

const mapStateToProps = (state) =>{
    return{
        whichNews : state.newsState
