import React, { Component } from 'react';
import {Link, NavLink, withRouter} from 'react-router-dom';
import {Button, Nav, Navbar, NavDropdown, Form, FormControl, Dropdown} from 'react-bootstrap';
import './navbar.css';
import { render } from '@testing-library/react';
import axios from 'axios';
import _ from 'lodash';
import Select from 'react-select';
import { connect } from 'react-redux';
import  {FaBookmark} from 'react-icons/fa';
import {FaRegBookmark} from 'react-icons/fa';


class CustomNavbar extends Component{
  state= {
        searchWord : "",
        searchResults : [],
        searchingWord : "",
        news : ""
    }

  onsearch = (selectedOption) =>{
    console.log("Callling only onceeee..")
    if (selectedOption.value) {
      let word = selectedOption.value;
      this.setState({ searchWord: "" })
      this.props.history.push({
          pathname: "/search/"+word,
          state: { searchWord: word }
      });
  } else {
      alert("No Text Entered");
  }
}

handleSearch = (inputValue) =>{
      const headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json',
        'Ocp-Apim-Subscription-Key':'d70e0b68e0b246e9bb36efc7d644ccb0'
        }
        { console.log("Input",inputValue) }
        this.setState({
          searchWord : inputValue
        })
        let url = `https://api.cognitive.microsoft.com/bing/v7.0/suggestions?q=${inputValue}`;
        axios.get(url, {headers:headers})
        .then(responseData => {
            let results = responseData.data.suggestionGroups[0].searchSuggestions
            const searchResults = results.map(result => ({ value: result.displayText, label: result.displayText }));
            this.setState({ searchResults })
            console.log("results",this.state.searchResults)
            return searchResults
          })
        .catch(responseData=>{
            console.log("ERROR from Search Results"); 
        })
        }

writeNews= (news) =>{
console.log("News to write", news);
axios.post('http://localhost:5000/setNewsdata',{news: news})
.then(response =>{
  console.log("Done", response.data)
})
.catch(responseData=>{
  console.log("error");
})
}

handleSwitchChange=()=>{
  if(this.props.whichNews === 'guardian' || this.props.whichNews === 'Guardian'){
    console.log("changing from", this.state.news,"to nytimes")
    this.setState({news:'nytimes'})
    this.props.changeNewsState('nytimes')
    this.writeNews('nytimes')
  }
  else{
    console.log("changing from", this.state.news,"to guardian")
    this.setState({news:'guardian'})
    this.props.changeNewsState('guardian')
    this.writeNews('guardian')
  }
}

handleFavorites=()=>{
  console.log("Going to Bookmarks")
  this.props.history.push({
    pathname: "/favorites"
  })
}

  render(){
    let bookmark = "";
    let switchNews = "";
    console.log("DETAILED ARTICLE REGEX CHECK", window.location.pathname.includes('/DetailedArticle'))
    //Bookmark handling
    if(window.location.pathname == '/favorites'){
      bookmark = (<Button className='mr-auto mb-2' onClick={()=>this.handleFavorites()} style={{background:'none', border:'none', paddingLeft:'0px'}}><FaBookmark style={{ height:'20px', width:'20px', color:'white'}}/></Button>)
    }
    else if(window.location.pathname == '/search' || window.location.pathname.includes('/DetailedArticle')){
      bookmark = (<Button className='mr-auto mb-2' onClick={()=>this.handleFavorites()} style={{background:'none', border:'none', paddingLeft:'0px'}}><FaRegBookmark style={{ height:'20px', width:'20px', color:'white'}}/></Button>)
    }
    else{
      bookmark = (<Button className='mr-auto mb-2 mr-lg-4' onClick={()=>this.handleFavorites()} style={{background:'none', border:'none', paddingLeft:'0px'}}><FaRegBookmark style={{ height:'20px', width:'20px', color:'white'}}/></Button>)
    }
    // Switch Handling
    if(window.location.pathname == '/favorites' || window.location.pathname == '/search' || window.location.pathname.includes('/DetailedArticle')){
      switchNews = ""
    }
    else{
      if(this.props.whichNews === 'nytimes'){
        console.log("THIS IS INISDE SWITCH", this.props.whichNews, "NYT") 
      switchNews = (
            <>
              <Navbar.Text className="mr-sm-2"> NY Times </Navbar.Text>
              <Form inline>
                  <Form.Check type="switch" id="custom-switch" className="custom-font-guardian" label="" onClick={this.handleSwitchChange}/>
              </Form>
              <Navbar.Text className="mr-sm-2"> Guardian </Navbar.Text>
            </>
      )
    }
      else{
        console.log("THIS IS INISDE SWITCH", this.props.whichNews, "GUA")
        switchNews = (
          <>
            <Navbar.Text className="mr-sm-2"> NY Times </Navbar.Text>
            <Form inline>
                <Form.Check type="switch" id="custom-switch" className="custom-font-guardian" label="" defaultChecked onClick={this.handleSwitchChange}/>
            </Form>
            <Navbar.Text className="mr-sm-2"> Guardian </Navbar.Text>
          </>
    )
  }

    }
      return(
      // On small it collapses 
          <Navbar className="bg-custom" expand="sm" variant="dark">
             <Select className="CustomSearchBar"
              onInputChange={_.debounce(this.handleSearch, 500)}
              value={this.searchWord}
              onChange={this.onsearch}
              options = { this.state.searchResults }
              placeholder="Enter Keyword .."
              />
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link as={Link} to="/" href="/">Home</Nav.Link>
                <Nav.Link as={Link} to="/world" href="/world" >World</Nav.Link>
                <Nav.Link as={Link} to="/politics" href="/politics" >Politics</Nav.Link>
                <Nav.Link as={Link} to="/business" href="/business" >Business</Nav.Link>
                <Nav.Link as={Link} to="/technology" href="/technology" >Technology</Nav.Link>
                <Nav.Link as={Link} to="/sports" href="/sports" >Sports</Nav.Link>
              </Nav>
              <Nav>
              {bookmark}
              {switchNews}
              </Nav>
            </Navbar.Collapse>
          </Navbar>
      )
      }
}


const mapStateToProps = (state) =>{
  return{
      whichNews : state.newsState
  }
}

const mapDispatchToProps = (dispatch) => {
  return{
  changeNewsState : (newsValue) => { dispatch({type: 'CHANGE_NEWS', newsValue: newsValue})}
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(withRouter(CustomNavbar))
