import React, { Component } from 'react';
import {Link, NavLink, withRouter} from 'react-router-dom';
import {Button, Nav, Navbar, NavDropdown, Form, FormControl, Dropdown} from 'react-bootstrap';
import './navbar.css';
import { render } from '@testing-library/react';
// import { Search} from 'semantic-ui-react';
import axios from 'axios';
import _ from 'lodash';
import Select from 'react-select';
import AsyncSelect from 'react-select/async';



class CustomNavbar extends Component{
  state= {
        searchWord : "",
        searchResults : [],
        searchingWord : ""
    }

  // onsearch = (word) =>{
  //   if (word) {
  //     let word = word;
  //     this.setState({ searchWord: "" })
  //     this.props.history.push({
  //         pathname: "/search",
  //         state: { searchWord: word }
  //     });
  // } else {
  //     alert("No Text Entered");
  // }

handleSearch = (inputValue) =>{
      const headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json',
        'Ocp-Apim-Subscription-Key':'d70e0b68e0b246e9bb36efc7d644ccb0'
        }
        { console.log("Input",inputValue) }
        this.setState({
          searchWord : inputValue
        })
        // let url = `https://api.cognitive.microsoft.com/bing/v7.0/suggestions?q=${ this.state.searchingWord}`;
        let url = `https://api.cognitive.microsoft.com/bing/v7.0/suggestions?q=${inputValue}`;
        axios.get(url, {headers:headers})
        .then(responseData => {
            let results = responseData.data.suggestionGroups[0].searchSuggestions
            const searchResults = results.map(result => ({ value: result.displayText, label: result.displayText }));
            this.setState({ searchResults })
            console.log("results",this.state.searchResults)
            return searchResults
          })
        .catch(responseData=>{
            console.log("ERROR from Search Results"); 
        })
        }

printout = (selectedOption) =>{
  console.log(selectedOption)
}

  render(){
      return(
      // On small it collapses 
          <Navbar className="bg-custom" expand="sm" variant="dark">
             <Select className="CustomSearchBar"
              onInputChange={_.debounce(this.handleSearch, 300)}
              //value = {_.debounce(this.handleSearch, 300)}
              onChange={this.printout}
              // onChange = {opt => console.log(opt.label, opt.value)}
              options = { this.state.searchResults }
              />
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link as={Link} to="/" href="/">Home</Nav.Link>
                <Nav.Link as={Link} to="/world" href="/world" >World</Nav.Link>
                <Nav.Link as={Link} to="/politics" href="/politics" >Politics</Nav.Link>
                <Nav.Link as={Link} to="/business" href="/business" >Business</Nav.Link>
                <Nav.Link as={Link} to="/technology" href="/technology" >Technology</Nav.Link>
                <Nav.Link as={Link} to="/sports" href="/sports" >Sports</Nav.Link>
              </Nav>
              <Nav>
              <Form inline>
                  <Navbar.Text className="mr-sm-2"> NY Times </Navbar.Text>
                  <Form.Check type="switch" id="custom-switch" className="custom-font-guardian" label="Guardian" defaultChecked/>
              </Form>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
      )
      }
}

export default withRouter(CustomNavbar)
