                                                <TwitterShareButton url={articleUrl} hashtags={['CSCI_571_NewsApp']}>
                                                    <TwitterIcon size={30} round={true}/>
                                                </TwitterShareButton>
                                                <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                                    <EmailIcon size={30} round={true}/>
                                                </EmailShareButton>
                                            </span>
                                           <Button onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'GUARDIAN',{articleDate})} className="justify-content-end" style={{position:'relative', left:'88%', backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button>
                                        </Card.Text>
                                    </Card.Title>
                                    <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                    <Accordion>
                                        <Card style={{border:'none'}}>
                                            <Card.Body style={{padding:'0px'}}>{desc_visible}</Card.Body>
                                            <Accordion.Collapse eventKey="0">
                                                <Card.Body style={{padding:'0px'}}>{desc_hidden}</Card.Body>
                                            </Accordion.Collapse>
                                            <Card.Header style={{backgroundColor:'white'}}>
                                                <Accordion.Toggle eventKey="0" onClick={()=>{this.handleToggle()}} className="float-right" style={{backgroundColor:'white', border:'none'}}>
                                                    <span style={{display:this.state.toggleMenuDown}}><FaChevronDown/></span>
