                                            <Button onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'NYTIMES',{articleDate})} className="justify-content-end" style={{position:'relative', left:'88%', backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button>
                                        </Card.Text>
