import React, { Component } from 'react'
import {Card, Col, Row, Badge, ModalBody, ModalTitle, Button, Modal} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './home.css';
import axios from 'axios';
import { FaShareAlt } from "react-icons/fa";
// import Modal from 'react-modal';

class Home extends Component{
    state= {
        newsHeadlines: [],
        newsAvailable: false,
        isopenModal : false
    }
    componentWillMount(){
        axios.get('http://localhost:5000/')
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    newsHeadlines : responseData.data,
                    newsAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
    }


    toggleModal = (e) =>{
        e.preventDefault();
        alert("called")
        this.setState({
            isopenModal : !this.state.isopenModal
        })
    }
    // searchModal = (e) =>{
    //     e.stopPropagation();
    //     e.nativeEvent.stopImmediatePropagation();
    //     console.log("Stopped")
    // }
        render(){
            if (!this.state.newsAvailable){
                return <div>Loading...</div>
            }
            else{
            const newsObjs = this.state.newsHeadlines
            const newsCards = this.state.newsHeadlines.map(news => {
                        if(news.sectionId === 'sport'){
                            let badgeVariant = "warning";
                        }
                        else if(news.sectionId === 'world'){
                            let badgeVariant = "primary";
                        }
                        else if(news.sectionId === 'technology'){
                            let badgeVariant = "success";
                        }
                        else if(news.sectionId === 'politics'){
                            let badgeVariant = "info";
                        }
                        else if(news.sectionId === 'business'){
                            let badgeVariant = "primary";
                        }
                        else {
                            let badgeVariant = "secondary";
                        }

                        if(news.blocks.hasOwnProperty('main') && !news.blocks.main.elements[0].assets.length == 0){
                        const newsTitle = news.webTitle;
                        const newsDescription = news.blocks.body[0].bodyTextSummary;
                        const newsImage = news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file;
                        const newsDate = news.webPublicationDate;
                        const newsBadge = news.sectionId;

                        return (
                            // <Link to='/world' style={{ width:'100%', display:'block', textDecoration: 'none', color:'inherit' }}>
                            <div className="HomepageCard">
                            <Card style={{ width: '100%', marginTop:'30px', marginBottom:'30px', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)' }} >
                                    <Row>
                                        <Col lg={3} xs={12} className="mr-0 ml-0" align="center">
                                            <Card.Img className="img-responsive" variant="top" src={newsImage} style={{ padding: '6px', border:'1px solid lightgray', margin:'10px', height:'200px', width:'90%', borderBottomLeftRadius: 'calc(.25rem - 1px)', borderBottomRightRadius: 'calc(.25rem - 1px)' }} />
                                        </Col>
                                        <Col lg={9} xs={12} className="mr-0 ml-0">
                                            <Card.Body>
                                                <Link to="/world" className="stretched-link"></Link>
                                                <Card.Title><em>{newsTitle}</em><Button style={{zIndex:'1', position:'absolute'}} onClick={this.toggleModal}><FaShareAlt/></Button></Card.Title>
                                                <Card.Text className="news-description">
                                                    {newsDescription}
                                                </Card.Text>
                                                <Card.Text>
                                                <span> {newsDate} </span>
                                                <Badge variant="primary" className="float-right">
                                                    {newsBadge}
                                                </Badge>
                                                </Card.Text>
                                            </Card.Body>

                                            <Modal show={this.state.isopenModal}>
                                                <ModalDialog>
                                                <Modal.Header toggle={this.toggleModal.bind(this)} closeButton>
                                                <Modal.Title>Modal heading</Modal.Title>
                                                </Modal.Header>
                                                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                                                </ModalDialog>
                                            </Modal>

                                        </Col>
                                    </Row>
                                </Card>
                            </div>
                            // </Link>
                      )
                    }
                })
            console.log(newsCards)
            return (
            <>
                {newsCards}
            </>
            );
        }
    }
}

export default Home;
