import React, { Component } from 'react'
import {Card, Col, Row, Badge, Button, Accordion} from 'react-bootstrap';
import './detailed_page.css';
import axios from 'axios';
import {FacebookShareButton, TwitterShareButton, EmailShareButton} from 'react-share';
import {EmailIcon,FacebookIcon,TwitterIcon} from 'react-share';
import { FaChevronDown } from "react-icons/fa";
import { FaChevronUp } from "react-icons/fa";
import { Thumbnail } from 'react-bootstrap';
import PageWithComments from './comments';
// import Comments from './commentsnew';
import { connect } from 'react-redux';
import {FaRegBookmark} from 'react-icons/fa';
import {FaBookmark} from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { ToastContainer,toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'
import BounceLoader from "react-spinners/BounceLoader";
import ReactTooltip from 'react-tooltip'


class Detailed extends Component{
    state= {
        id : "",
        toggleMenuDown : 'flex',
        toggleMenuUp : 'none',
        articleDetails: null,
        articleAvailable: false,
        bookmarkset : false,
        articleNewsSource : ""
    }
// Toggle for News Description
    handleToggle = () =>{
        let down = this.state.toggleMenuUp 
        let up = this.state.toggleMenuDown 
        this.setState({
            toggleMenuDown : down,
            toggleMenuUp : up
        })
    }
// Getting the article Data

    componentWillMount(){
        let urlPath = window.location.pathname
        let urlPathId = urlPath.slice(21)
        let urlNews = this.props.match.params.news
        console.log("From news", urlNews)
        console.log("URL PARAMETERS",urlPathId)
        // Checkin if bookmarked or not
        let dataFetch = localStorage.getItem('favoriteData')
        let localData = dataFetch.length!=0?JSON.parse(dataFetch):[]
        console.log("Bookmark local data", localData)
        if (localData.length!=0){
            let filteredData = localData.filter(article=>article.id==urlPathId)
            console.log("Bookmarked filter data", filteredData)
            if(filteredData.length==0){
                this.setState({
                    bookmarkset : false,
                })
            }
            else{
                this.setState({
                    bookmarkset : true,
                })
            }
        }
        else{
        this.setState({
            bookmarkset : false,
        })
        }

        this.setState({
            id:urlPathId,
        })

        console.log("Its coming from this message,use this",this.props.match.params.news )
        if(urlNews == 'GUA'){
            console.log("Inside Axios")
        let url = 'http://localhost:5000/article/' + urlPathId
        axios.get(url)
            .then(responseData => {
                console.log(responseData.data);
                this.setState({
                    articleDetails : responseData.data,
                    articleAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })
        }
        else if(urlNews == 'NYT'){
        let url = 'http://localhost:5000/nyarticle/' + urlPathId
        axios.get(url)
            .then(responseData => {
                console.log("NY ARTICLE",responseData.data);
                this.setState({
                    articleDetails : responseData.data,
                    articleAvailable : true
                })
            })
            .catch(responseData=>{
                console.log("error");
            })

        }
    }

// Handling Bookmark

    handleBookmark = (e,title, image, news, date, section) =>{
        let localdataFetch = localStorage.getItem('favoriteData')
        let localdata = localdataFetch?JSON.parse(localdataFetch):[]
        if(localdata.length != 0){
        let fetchArticle = localdata.filter(f_article =>{return f_article.id === this.state.id})
        console.log("Article Fetched",fetchArticle)
        let alreadyThere = fetchArticle.length>0?true:false
        console.log("There",alreadyThere)

        if(alreadyThere){
            // Remove it
            this.setState({
                bookmarkset:false
            })
            let toastData = 'Removing-' + title.articleTitle
            toast(toastData)
            let data = localdata.filter(f_article =>{
                console.log("f_article",f_article.id)
                // console.log('Article ID', this.state.id)
                return f_article.id != this.state.id})
            console.log("Already Present-So removing", data)
            localStorage.setItem('favoriteData', JSON.stringify(data))

        }
        else{
            this.setState({
                bookmarkset:true
            })
            let toastData = 'Saving-' + title.articleTitle
            toast(toastData)
            let favoriteTitle = title.articleTitle
            let favoriteImage = image.articleImage
            let favoriteDate = date.articleDate
            let favoriteNews = news
            let favoriteId = this.state.id
            let favoriteSection = section.articleSection
            // Add article
            let data = [...localdata, {title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews,id:favoriteId,section:favoriteSection}]
            console.log("Not There- So adding",data)
            localStorage.setItem('favoriteData', JSON.stringify(data))
        }

    }
    else{
        this.setState({
            bookmarkset:true
        })
        let toastData = 'Saving-' + title.articleTitle
        toast(toastData)
        let favoriteTitle = title.articleTitle
        let favoriteImage = image.articleImage
        let favoriteDate = date.articleDate
        let favoriteNews = news
        let favoriteId = this.state.id
        let favoriteSection = section.articleSection
        // Add article
        let data = [...localdata, {title:favoriteTitle,image:favoriteImage,date:favoriteDate,news:favoriteNews,id:favoriteId,section:favoriteSection}]
        console.log("No data- So adding 1st Element", data)
        localStorage.setItem('favoriteData', JSON.stringify(data))

    }
}
    render(){
        let bookmarkButtonG = ""
        let bookmarkButtonN = ""
        console.log("Bookmarked:",this.state.bookmarkset)
        if (!this.state.articleAvailable){
            return(
                <div className="sweet-loading" style = {{position:'fixed', top:'50%', left:'47%'}}>
                    <BounceLoader
                    style = {{display:'block', background:'#2b4fc4'}}
                    size={35}
                    color={"#2b4fc4"}
                    />
                <span style={{position:'fixed',left:'46.5%', fontSize:'14px'}}>Loading</span>
            </div>
            )
        }
        else{
            if(this.props.match.params.news == 'GUA'){
                console.log("inside render->ArticleDetails", this.state.articleDetails)
            const articleObj = this.state.articleDetails
            const articleTitle = articleObj.webTitle
            // const articleImage = articleObj.blocks.main.elements[0].assets[articleObj.blocks.main.elements[0].assets.length-1].file
            let imageList = []
                if(articleObj.blocks.main.elements[0].assets.length == 0){
                    imageList = ["https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png"]
                }
                else{
                    let filteredList = articleObj.blocks.main.elements[0].assets.filter(image => {
                    return image.typeData.width >= 2000
                    })
                    imageList = filteredList.map(filteredimg=>{
                    return filteredimg.file
                    })
                    if(imageList.length == 0){
                        imageList = ["https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png"]
                    }
                }
            const articleImage = imageList[0]
            console.log("Image List",imageList)
            let articleDate = articleObj.webPublicationDate
            articleDate = articleDate.slice(0,10)
            const articleUrl = articleObj.webUrl
            let articleSection = articleObj.sectionId
            const articleDescription = articleObj.blocks.body[0].bodyTextSummary
            let Description = articleDescription.match(/([^\.!\?]+[\.!\?]+)|([^\.!\?]+$)/g);
            {console.log(Description)}
            if(Description){
                Description = Description
            }
            else{
                Description = "No Description"
                Description = Description.match(/([^\.!\?]+[\.!\?]+)|([^\.!\?]+$)/g);
            }
            if(this.state.bookmarkset){
                bookmarkButtonG = (<Button data-tip="Bookmark" onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'GUARDIAN',{articleDate},{articleSection})} className="ml-4" style={{backgroundColor:'white', border:'none'}}><span><FaBookmark style={{color:'red'}}/></span></Button>)
            }
            else{
                bookmarkButtonG = (<Button data-tip="Bookmark" onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'GUARDIAN',{articleDate},{articleSection})} className="ml-4" style={{backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button>)
            }

            if(Description.length<=4){
            return(
                <>
                    <Col lg={12} sm={12}>
                            <Card className="SearchCard" style={{borderRadius:'0', boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)'}}>
                            <Card.Body>
                                <Card.Title><em style={{fontWeight:'initial', fontSize:'18px'}}>{articleTitle}</em>
                                    <Card.Text className="mt-2">
                                        <span><em style={{fontWeight:'initial', fontSize:'16px'}}> {articleDate}</em></span>
                                        <span className="justify-content-end float-right">
                                        <ReactTooltip />
                                        <FacebookShareButton url={articleUrl} hashtag='CSCI_571_NewsApp' className="share">
                                            <FacebookIcon data-tip="Facebook" size={30} round={true}/>
                                        </FacebookShareButton>
                                        <TwitterShareButton url={articleUrl} hashtags={['CSCI_571_NewsApp']}>
                                            <TwitterIcon data-tip="Twitter" size={30} round={true}/>
                                        </TwitterShareButton>
                                        <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                            <EmailIcon data-tip="Email" size={30} round={true}/>
                                        </EmailShareButton>
                                        {/* <Button onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'GUARDIAN',{articleDate},{articleSection})} className="ml-4" style={{backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button> */}
                                        {bookmarkButtonG}
                                        </span>
                                        <ToastContainer position={toast.POSITION.TOP_CENTER} autoClose={3000}/>
                                    </Card.Text>
                                </Card.Title>
                                <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                <Accordion>
                                    <Card style={{border:'none'}}>
                                        <Card.Body style={{padding:'0px'}}>{articleDescription}</Card.Body>
                                    </Card>
                                </Accordion>
                            </Card.Body>
                            </Card >
                    </Col>
                    <br></br>
                    <PageWithComments id={window.location.pathname} />
                </>
            )
            }

            // ==========================================================================================================
            else{
                // let card = []
                let i = 0
                let desc_visible = ""
                for(i=0;i<4;i++){
                    desc_visible += Description[i]
                }
                let desc_hidden = ""
                for(i=4;i<Description.length;i++){
                    desc_hidden += Description[i]
                }
                            return(
                                <>
                                <Col lg={12} sm={12}>
                                <Card className="SearchCard" style={{borderRadius:'0',boxShadow:'0 6px 20px 0 rgba(0, 0, 0, 0.55)'}}>
                                <Card.Body>
                                    <Card.Title><em style={{fontWeight:'initial', fontSize:'18px'}}>{articleTitle}</em>
                                        <Card.Text className="mt-2">
                                            <span><em style={{fontWeight:'initial', fontSize:'16px'}}>{articleDate}</em></span>
                                            <span className="justify-content-end float-right">
                                                <ReactTooltip />
                                                <FacebookShareButton url={articleUrl} hashtag='CSCI_571_NewsApp' className="share">
                                                    <FacebookIcon data-tip="Facebook" size={30} round={true}/>
                                                </FacebookShareButton>
                                                <TwitterShareButton url={articleUrl} hashtags={['CSCI_571_NewsApp']}>
                                                    <TwitterIcon data-tip="Twitter" size={30} round={true}/>
                                                </TwitterShareButton>
                                                <EmailShareButton url={articleUrl} subject='#CSCI_571_NewsApp'>
                                                    <EmailIcon data-tip="Email" size={30} round={true}/>
                                                </EmailShareButton>
                                                {/* <Button onClick={(e)=>this.handleBookmark(e,{articleTitle},{articleImage},'GUARDIAN',{articleDate},{articleSection})} className="ml-4" style={{backgroundColor:'white', border:'none'}}><span><FaRegBookmark style={{color:'red'}}/></span></Button> */}
                                                {bookmarkButtonG}
                                            </span>
                                           <ToastContainer position={toast.POSITION.TOP_CENTER} autoClose={3000}/>
                                        </Card.Text>
                                    </Card.Title>
                                    <Card.Img variant="top" src={articleImage} className="DetailedCardImg"/>
                                    <Accordion>
                                        <Card style={{border:'none'}}>
                                            <Card.Body style={{padding:'0px'}}>{desc_visible}</Card.Body>
                                            <Accordion.Collapse eventKey="0">
                                                <Card.Body style={{padding:'0px'}}>{desc_hidden}</Card.Body>
                                            </Accordion.Collapse>
