import React, { Component } from 'react'
import {Card, Col, Row, Badge} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './search.css';
import axios from 'axios';

class searchResults extends Component{
    state= {
        searchResults: [],
        newsAvailable: false
    }
    componentWillMount(){
        let url = 'http://localhost:5000/searchResults/' + 'Tesla'
        axios.get(url)
        .then(responseData => {
            console.log(responseData.data);
            this.setState({
                searchResults : responseData.data,
                newsAvailable : true
            })
        })
        .catch(responseData=>{
            console.log("ERROR from Search Results");
            })
    }
    render(){
        if (!this.state.newsAvailable){
            return <div>Loading...</div>
        }
        else{
        const newsObjs = this.state.searchResults
        const newsCards = this.state.searchResults.map(news => {
                    if(news.sectionId === 'sport'){
                        let badgeVariant = "warning";
                    }
                    else if(news.sectionId === 'world'){
                        let badgeVariant = "primary";
                    }
                    else if(news.sectionId === 'technology'){
                        let badgeVariant = "success";
                    }
                    else if(news.sectionId === 'politics'){
                        let badgeVariant = "info";
                    }
                    else if(news.sectionId === 'business'){
                        let badgeVariant = "primary";
                    }
                    else {
                        let badgeVariant = "secondary";
                    }
                    if(!news.blocks.main.elements[0].assets.length == 0){
                        console.log("Not present", news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file)
                    console.log("Data")
                    const newsTitle = news.webTitle;
                    const newsDescription = news.blocks.body[0].bodyTextSummary;
                    const newsImage = news.blocks.main.elements[0].assets[news.blocks.main.elements[0].assets.length-1].file;
                    console.log(newsImage)
                    const newsDate = news.webPublicationDate;
                    const newsBadge = news.sectionId;
                    return (
                        <Col lg={3} sm={12}>
                            <Card className="SearchCard">
                            <Card.Body>
                                <Card.Title>{newsTitle}</Card.Title>
                                <Card.Img variant="top" src={newsImage} className="SearchCardImg"/>
                                <Card.Text className="mt-2">
                                    <span> {newsDate} </span>
                                    <Badge variant="primary" className="float-right">
                                        news
                                    </Badge>
                                </Card.Text>
                            </Card.Body>
                            </Card >
                        </Col>
                        )
                    }
                })
        console.log(newsCards)
        return (
        <>
        <div style={{marginLeft:'20px', marginRight:'20px'}}>
                <h2 style={{marginTop:'3px', marginBottom:'0px'}}>Results</h2>
                <Row>
                    {newsCards}
                </Row>
        </div>
        </>
        );
    }
}
}

export default searchResults;
