import React from 'react';
import commentBox from 'commentbox.io';
import './home.css'

class PageWithComments extends React.Component {
    state = {
        id_value:""
    }
    componentDidMount() {
        this.setState({
            id_value:this.props.id
        })
        this.removeCommentBox = commentBox('5725357550338048-proj',{
                tlcParam: 'tlc-id', // used for identifying links to comments on your page
                backgroundColor: 'lightblue', // default transparent
                textColor: 'green', // default black
                subtextColor: 'green', // default grey
                singleSignOn: null, // enables Single Sign-On (for Professional plans only)
                /**
                 * Creates a unique URL to each box on your page.
                 * 
                // @param {string} boxId
                // @param {string} pageLocation - a copy of the current window.location
                // @returns {string}
                //  */
                createBoxUrl(boxId, pageLocation) {
                    pageLocation.search = 'DetailedArticle/GUA/'; // removes query string!
                    pageLocation.hash = boxId; // creates link to this specific Comment Box on your page
                    console.log("Inside createBoxUrl search:",pageLocation.search,"\nID",boxId,"\nPagelocation",pageLocation,"\nPagelocation href",pageLocation.href,'\nPagelocation hash',pageLocation.hash)
                    return pageLocation.href; // return url string

                }});
    }

    // componentWillUnmount() {

    //     this.removeCommentBox();
    // }

    render() {
        let id = '/DetailedArticle/GUA' + this.state.id_value
        return (
            <div className="commentbox" id={id}/>
        );
    }
}

export default PageWithComments




